const t=[];chrome.webRequest.onCompleted.addListener(e=>{if(e.url.startsWith("chrome-extension://"))return;const r={url:e.url,method:e.method,statusCode:e.statusCode,type:e.type,timeStamp:new Date(e.timeStamp).toLocaleTimeString()};t.push(r),t.length>100&&t.shift(),chrome.runtime.sendMessage({type:"NETWORK_LOG_UPDATE",payload:r})},{urls:["<all_urls>"]});chrome.runtime.onMessage.addListener((e,r,o)=>{e.type==="GET_NETWORK_LOGS"&&o({logs:t})});
